package operators;

public class LifeWithoutLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int var; //declaration
		var=1; // initialization
		int var1=1; //declaration + initialization
		
		
		//When you have to run some set of statement /code multiple time then use loop
		//now we have some condition to tell java hoe many times we have to iterate or execute the code
		
		for(int i=1;i<=10;i++)
			//I have one variable whose name is i and its value is 1
			//Please run the code till ii value is either less than 10 or equal to 10 
			
			
		{
			System.out.println(i);
			
		}
		//System.out.println("I am out of for loop");
	}

}
