package operators;

public class dowhileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int s=1;
		//multiple comment ctrl+shift+/
		//multiple comment ctrl+shift+\
		
		do {
			
			System.out.println(s);
			s++;
			
		}while(s<=10);
		
	}

}
